import React from "react";
import {StyleSheet} from "@react-pdf/renderer";

const styles = StyleSheet.create({
    table: {
        // width: "80%",
        margin: "0 10px",
        border: "1px solid black",
        borderCollapse: "collapse"
    },
    th: {
        border: "1px solid black",
        textAlign: "center"
    },
    td: {
        border: "1px solid black",
        textAlign: "center"
    }
});

export default function SimpleTable() {
    const [data, updateData] = React.useState([]);
    const [firstLoad, setLoad] = React.useState(true);
    let isLoading = true;

    function sampleFunc() {
        fetch("/resumes")
            .then(response => response.json())
            .then(json => {
                let resumeList = json._embedded.resumeList;
                updateData(resumeList);
                console.log(resumeList);
                console.log("embedded data: " + data);
            });
    }

    if (firstLoad) {
        sampleFunc();
        setLoad(false);
        console.log("firstLoad is false")
    }

    console.log("data is now: " + data + "; data length is now " + data.length);
    if (data.length > 0) {
        isLoading = false;
        console.log("data length > 0")
    }

    return (
        <div>
            <h3>
                Resume Directory
            </h3>

            {isLoading ? (
                <h5>Loading</h5>
            ) : (
                <table style={styles.table}>
                    <tr>
                        <th style={styles.th}>Name</th>
                        <th style={styles.th}>Contact Info</th>
                        <th style={styles.th}>Education</th>
                        <th style={styles.th}>Skills</th>
                        <th style={styles.th}>Experiences</th>
                        <th style={styles.th}>Projects</th>
                        <th style={styles.th}>Links</th>
                    </tr>
                    {data?.map(row => (
                        <tr key={row.name}>
                            <td style={styles.td}>{row.name}</td>
                            <td style={styles.td}>
                                {row.contactInfo.phoneNumber + "\n" + row.contactInfo.email + "\n" + row.contactInfo.linkedIn}
                            </td>
                            <td style={styles.td}>{row.education.school + "\n" + row.education.degree + "\n" + row.education.major
                            + "\n" + row.education.minor + "\n" + row.education.gpa}</td>
                            <td style={styles.td}>{row.skills.map(item => item + "\n")}</td>
                            <td style={styles.td}>{row.experiences.map(item => item.title + "\n" + item.company + "\n"
                            + item.date + "\n" + item.description + "\n")}</td>
                            <td style={styles.td}>{row.projects.map(item => item.name + "\n" + item.date + "\n" +
                            item.description + "\n")}</td>
                            <td style={styles.td}>
                                <a href={"/pdf/" + row.id}>
                                    Pdf + {row.name}
                                </a>
                            </td>
                        </tr>
                    ))}
                </table>
            )}
            <a href={"/"}>Head back to save data</a>
        </div>
    );
}
