import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import { Link } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import {StyleSheet} from "@react-pdf/renderer";

const styles = StyleSheet.create({
    table: {
        // width: "80%",
        margin: "0 10px"
    }
});

export default function SimpleTable() {
    const [data, updateData] = React.useState([]);
    const [firstLoad, setLoad] = React.useState(true);
    let isLoading = true;

    function sampleFunc() {
        fetch("/resumes")
            .then(response => response.json())
            .then(json => {
                let resumeList = json._embedded.resumeList;
                updateData(resumeList);
                console.log(resumeList);
                console.log("embedded data: " + data);
            });
    }

    if (firstLoad) {
        sampleFunc();
        setLoad(false);
        console.log("firstLoad is false")
    }

    console.log("data is now: " + data + "; data length is now " + data.length);
    if (data.length > 0) {
        isLoading = false;
        console.log("data length > 0")
    }

    return (
        <div>
            <h5>
                Resume Directory
            </h5>

            {isLoading ? (
                <h5>Loading</h5>
            ) : (
                <table style={styles.table}>
                    <tr>
                        <th align="center">Name</th>
                        <th align="center">Contact Info</th>
                        <th align="center">Education</th>
                        <th align="center">Skills</th>
                        <th align="center">Experiences</th>
                        <th align="center">Projects</th>
                        <th align="center">Links</th>
                    </tr>
                    {data?.map(row => (
                        <tr key={row.name}>
                            <td align="center">{row.name}</td>
                            <td align="center">
                                {row.contactInfo.phoneNumber + "\n" + row.contactInfo.email + "\n" + row.contactInfo.linkedIn}
                            </td>
                            <td align="center">{row.education.school + "\n" + row.education.degree + "\n" + row.education.major
                            + "\n" + row.education.minor + "\n" + row.education.gpa}</td>
                            <td align="center">{row.skills.map(item => item + "\n")}</td>
                            <td align="center">{row.experiences.map(item => item.title + "\n" + item.company + "\n"
                            + item.date + "\n" + item.description + "\n")}</td>
                            <td align="center">{row.projects.map(item => item.name + "\n" + item.date + "\n" +
                            item.description + "\n")}</td>
                            <td align="center">
                                <a href={"/pdf/" + row.id}>
                                    Pdf + {row.name}
                                </a>
                            </td>
                        </tr>
                    ))}
                </table>
            )}
            <a href={"/"}>Head back to save data</a>
        </div>
    );
}
