import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

export default function AddResume() {
    const classes = useStyles();
    const [firstLoad, setLoad] = React.useState(true);

    const [name, setName] = React.useState("N/A");

    const [email, setEmail] = React.useState("");
    const [phoneNumber, setPhoneNumber] = React.useState("");
    const [linkedIn, setLinkedIn] = React.useState("");

    const [school, setSchool] = React.useState("");
    const [degree, setDegree] = React.useState("");
    const [major, setMajor] = React.useState("");
    const [minor, setMinor] = React.useState("");
    const [gpa, setGpa] = React.useState("");

    const [skill1, setSkill1] = React.useState("");
    const [skill2, setSkill2] = React.useState("");
    const [skill3, setSkill3] = React.useState("");

    const [experiences, setExperiences] = React.useState("");
    const [projects, setProjects] = React.useState("");

    const handleNameChange = event => setName(event.target.value);

    const handleEmailChange = event => setEmail(event.target.value);
    const handlePhoneNumberChange = event => setPhoneNumber(event.target.value);
    const handleLinkedInChange = event => setLinkedIn(event.target.value);

    const handleSchoolChange = event => setSchool(event.target.value);
    const handleDegreeChange = event => setDegree(event.target.value);
    const handleMajorChange = event => setMajor(event.target.value);
    const handleMinorChange = event => setMinor(event.target.value);
    const handleGpaChange = event => setGpa(event.target.value);

    const handleSkill1Change = event => setSkill1(event.target.value);
    const handleSkill2Change = event => setSkill2(event.target.value);
    const handleSkill3Change = event => setSkill3(event.target.value);

    const handleExperiencesChange = event => setExperiences(event.target.value);
    const handleProjectsChange = event => setProjects(event.target.value);

    const [message, setMessage] = React.useState("Nothing saved in the session");

    async function sampleFunc(toInput) {
        const response = await fetch("/resumes", {
            method: "POST",
            mode: "cors",
            cache: "no-cache",
            credentials: "same-origin",
            headers: {
                "Content-Type": "application/json"
            },
            redirect: "follow",
            referrerPolicy: "no-referrer",
            body: JSON.stringify(toInput)
        });

        let body = await response.json();
        console.log(body.id);
        setMessage(body.id ? "Data successfully updated" : "Data update failed");
    }

    const handleSubmit = variables => {
        const contactInfo = { email, phoneNumber, linkedIn }
        console.log(contactInfo);
        const education = { school, degree, major, minor, gpa }
        console.log(skills);
        const skills = ([]).concat(skill1).concat(skill2).concat(skill3);
        const toInput = { name, contactInfo, education, skills, experiences, projects };
        sampleFunc(toInput);
    };

    if (firstLoad) {
        setLoad(false)
    }

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <GroupIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Employee Directory
                </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="name"
                                value={name}
                                label="Name"
                                name="name"
                                autoComplete="name"
                                onChange={handleNameChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="email"
                                name="email"
                                variant="outlined"
                                required
                                fullWidth
                                value={email}
                                id="email"
                                label="Email"
                                onChange={handleEmailChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="phoneNumber"
                                name="phoneNumber"
                                variant="outlined"
                                required
                                fullWidth
                                value={phoneNumber}
                                id="phoneNumber"
                                label="Phone Number"
                                onChange={handlePhoneNumberChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="linkedIn"
                                name="linkedIn"
                                variant="outlined"
                                required
                                fullWidth
                                value={linkedIn}
                                id="linkedIn"
                                label="LinkedIn"
                                onChange={handleLinkedInChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="school"
                                value={school}
                                label="School"
                                name="school"
                                autoComplete="school"
                                onChange={handleSchoolChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="degree"
                                value={degree}
                                label="Degree"
                                name="degree"
                                autoComplete="degree"
                                onChange={handleDegreeChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="major"
                                value={major}
                                label="Major"
                                name="major"
                                autoComplete="major"
                                onChange={handleMajorChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="minor"
                                value={minor}
                                label="Minor"
                                name="minor"
                                autoComplete="minor"
                                onChange={handleMinorChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="gpa"
                                value={gpa}
                                label="GPA"
                                name="gpa"
                                autoComplete="gpa"
                                onChange={handleGpaChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="skill1"
                                value={skill1}
                                label="Skill 1"
                                name="skill1"
                                autoComplete="skill 1"
                                onChange={handleSkillsChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                // id="skills"
                                // value={skills}
                                label="Skills"
                                name="skills"
                                autoComplete="skills"
                                onChange={handleSkillsChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                // id="skills"
                                // value={skills}
                                label="Skills"
                                name="skills"
                                autoComplete="skills"
                                onChange={handleSkillsChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="experiences"
                                value={experiences}
                                label="Experiences"
                                name="experiences"
                                autoComplete="experiences"
                                onChange={handleExperiencesChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="projects"
                                value={projects}
                                label="Projects"
                                name="projects"
                                autoComplete="projects"
                                onChange={handleProjectsChange}
                            />
                        </Grid>
                    </Grid>
                    <Button
                        fullWidth
                        variant="contained"
                        color="primary"
                        preventDefault
                        className={classes.submit}
                        onClick={handleSubmit}
                    >
                        Save
                    </Button>

                    <Grid container justify="center">
                        <Grid item>
                            <Link to="/view">View Resume Records</Link>
                        </Grid>
                    </Grid>
                </form>
                <Typography style={{ margin: 7 }} variant="body1">
                    Status: {message}
                </Typography>
            </div>
        </Container>
    );
}
