import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import { Link } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";

const useStyles = makeStyles(theme => ({
    table: {
        minWidth: 600
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    paper: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        margin: `10px`,
        height: "100%",
        width: "99%",
        marginTop: theme.spacing(7)
    },
    link: {
        color: "rgba(0,0,0,0.65)",
        textDecoration: "none",
        marginLeft: "10%",
        alignSelf: "flex-start",
        "&:hover": {
            color: "rgba(0,0,0,1)"
        }
    }
}));

export default function SimpleTable() {
    const classes = useStyles();

    const [data, updateData] = React.useState([]);
    const [firstLoad, setLoad] = React.useState(true);
    let isLoading = true;

    function sampleFunc() {
        fetch("/resumes")
            .then(response => response.json())
            .then(json => {
                let resumeList = json._embedded.resumeList;
                updateData(resumeList);
                console.log(resumeList);
                console.log("embedded data: " + data);
            });
    }

    if (firstLoad) {
        sampleFunc();
        setLoad(false);
        console.log("firstLoad is false")
    }

    console.log("data is now: " + data + "; data length is now " + data.length);
    if (data.length > 0) {
        isLoading = false;
        console.log("data length > 0")
    }

    return (
        <div className={classes.paper}>
            <Avatar className={classes.avatar}>
                <GroupIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
                Resume Directory
            </Typography>

            {isLoading ? (
                <CircularProgress />
            ) : (
                <TableContainer
                    style={{ width: "80%", margin: "0 10px" }}
                    component={Paper}
                >
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell align="center">Name</TableCell>
                                <TableCell align="center">Contact Info</TableCell>
                                <TableCell align="center">Education</TableCell>
                                <TableCell align="center">Skills</TableCell>
                                <TableCell align="center">Experiences</TableCell>
                                <TableCell align="center">Projects</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {data?.map(row => (
                                <TableRow key={row.name}>
                                    <TableCell align="center">{row.name}</TableCell>
                                    <TableCell align="center">{row.contactInfo.phoneNumber}</TableCell>
                                    <TableCell align="center">{row.education}</TableCell>
                                    <TableCell align="center">{row.skills}</TableCell>
                                    <TableCell align="center">{row.experiences}</TableCell>
                                    <TableCell align="center">{row.projects}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            )}
            <Link className={classes.link} to="/">
                <Typography align="left">
                    &#x2190; Head back to save data
                </Typography>
            </Link>
        </div>
    );
}
