import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

export default function AddResume() {
    const classes = useStyles();
    const [firstLoad, setLoad] = React.useState(true);

    const [name, setName] = React.useState("N/A");

    // const [contactInfo, setContactInfo] = React.useState({});
    const [email, setEmail] = React.useState("");
    const [phoneNumber, setPhoneNumber] = React.useState("");
    const [linkedIn, setLinkedIn] = React.useState("");

    const [education, setEducation] = React.useState("");
    const [skills, setSkills] = React.useState("");
    const [experiences, setExperiences] = React.useState("");
    const [projects, setProjects] = React.useState("");

    const handleNameChange = event => setName(event.target.value);
    const handlePhoneNumberChange = event => setPhoneNumber(event.target.value);
    const handleEmailChange = event => setEmail(event.target.value);
    const handleLinkedInChange = event => setLinkedIn(event.target.value);
    const handleEducationChange = event => setEducation(event.target.value);
    const handleSkillsChange = event => setSkills(event.target.value);
    const handleExperiencesChange = event => setExperiences(event.target.value);
    const handleProjectsChange = event => setProjects(event.target.value);

    const [message, setMessage] = React.useState("Nothing saved in the session");

    async function sampleFunc(toInput) {
        const response = await fetch("/resumes", {
            method: "POST",
            mode: "cors",
            cache: "no-cache",
            credentials: "same-origin",
            headers: {
                "Content-Type": "application/json"
            },
            redirect: "follow",
            referrerPolicy: "no-referrer",
            body: JSON.stringify(toInput)
        });

        let body = await response.json();
        console.log(body.id);
        setMessage(body.id ? "Data successfully updated" : "Data update failed");
    }

    const handleSubmit = variables => {
        // const myContact = {
        //     "email": email,
        //     "phoneNumber": phoneNumber,
        //     "linkedIn": linkedIn };
        // const myContact = {email, phoneNumber, linkedIn};
        // console.log("old contact" + contactInfo);
        // setContactInfo(JSON.parse(JSON.stringify(myContact)));
        // console.log("new contact: " + contactInfo);
        // console.log("new string contact: " + JSON.stringify(contactInfo));

        const contactInfo = { email, phoneNumber, linkedIn }
        console.log(contactInfo);
        const toInput = { name, contactInfo, education, skills, experiences, projects };
        sampleFunc(toInput);
    };

    if (firstLoad) {
        setLoad(false)
    }

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <GroupIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Employee Directory
                </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="name"
                                value={name}
                                label="Name"
                                name="name"
                                autoComplete="name"
                                onChange={handleNameChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="phoneNumber"
                                name="phoneNumber"
                                variant="outlined"
                                required
                                fullWidth
                                value={phoneNumber}
                                id="phoneNumber"
                                label="Phone Number"
                                onChange={handlePhoneNumberChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="email"
                                name="email"
                                variant="outlined"
                                required
                                fullWidth
                                value={email}
                                id="email"
                                label="Email"
                                onChange={handleEmailChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                autoComplete="linkedIn"
                                name="linkedIn"
                                variant="outlined"
                                required
                                fullWidth
                                value={linkedIn}
                                id="linkedIn"
                                label="LinkedIn"
                                onChange={handleLinkedInChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="education"
                                value={education}
                                label="Education"
                                name="education"
                                autoComplete="education"
                                onChange={handleEducationChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="skills"
                                value={skills}
                                label="Skills"
                                name="skills"
                                autoComplete="skills"
                                onChange={handleSkillsChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="experiences"
                                value={experiences}
                                label="Experiences"
                                name="experiences"
                                autoComplete="experiences"
                                onChange={handleExperiencesChange}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="projects"
                                value={projects}
                                label="Projects"
                                name="projects"
                                autoComplete="projects"
                                onChange={handleProjectsChange}
                            />
                        </Grid>
                    </Grid>
                    <Button
                        fullWidth
                        variant="contained"
                        color="primary"
                        preventDefault
                        className={classes.submit}
                        onClick={handleSubmit}
                    >
                        Save
                    </Button>

                    <Grid container justify="center">
                        <Grid item>
                            <Link to="/view">View Resume Records</Link>
                        </Grid>
                    </Grid>
                </form>
                <Typography style={{ margin: 7 }} variant="body1">
                    Status: {message}
                </Typography>
            </div>
        </Container>
    );
}
